<html><body>	<?php//print out info gathered by HTML to printed in XML	echo "<html><body>";	echo "First Name: $_POST[first]<br>";	echo "Last Name: $_POST[last]<br>";	echo "Phone Number: $_POST[phone]<br>";	echo "Address: $_POST[address]<br>";	echo "Zip: $_POST[zip]<br>";	echo "Birthday: $_POST[birthday]<br>";//write to an XML structured file called skeltonoutput.txt	$file = fopen("userprofile.txt","w+");	fwrite($file, "<first>".$_POST['first']."</first>"."\n");	fwrite($file, "<last>".$_POST['last']."</last>"."\n");	fwrite($file, "<phone>".$_POST['phone']."</phone>"."\n");	fwrite($file, "<address>".$_POST['address']."</address>"."\n");	fwrite($file, "<zip>".$_POST['zip']."</zip> "."\n");	fwrite($file, "<birthday>".$_POST['birthday']."</birthday> "."\n");	fclose($file);	?></body></html>